import logo from "./logo.svg";
import "./App.css";
import AddButton from "./components/Button";

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <AddButton />
        <ul>
          <li>List component that shows existing link</li>
          <li>
            Add link that allows user to add link to
            application
          </li>
          <li>
            Search link which filters the user input on the
            content of the link
          </li>
          <li>
            An add link form that has validation on the
            input of the user{" "}
          </li>
          <li>
            Persist link data in Local Storage - stringify
            object, parse it on return{" "}
          </li>
          <li>
            Link should correspond to a model with url,
            title and tags{" "}
          </li>
        </ul>
      </header>
    </div>
  );
}

export default App;
