import React, { useState, useEffect } from "react";

function LinkTable() {
  return (
    <div>
      <table className="table table-striped">
        <thead>
          <tr>
            <th scope="col">id</th>
            <th scope="col">Title</th>
            <th scope="col">Link</th>
            <th scope="col">Tags</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <th scope="row">1</th>
            <td>Mark</td>
            <td>Otto</td>
            <td>@mdo</td>
          </tr>
          <tr>
            <th scope="row">2</th>
            <td>Jacob</td>
            <td>Thornton</td>
            <td>@fat</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}

function FilterInput() {
  return (
    <div className="md-form form-group mt-5">
      <input
        type="text"
        className="form-control"
        id="formGroupExampleInput2MD"
        placeholder="Filter"
      />
    </div>
  );
}

function AddLink(props) {
  return (
    <div>
      <div className="container my-5 py-5 z-depth-1">
        <section className="px-md-5 mx-md-5 text-center text-lg-left dark-grey-text">
          <div className="row d-flex justify-content-center">
            <form
              className="text-center"
              onSubmit={props.linkOnSubmit}
              method="post"
            >
              <p className="h4 mb-4">Add Link</p>
              <input
                type="text"
                name="title"
                onChange={props.titleOnChange}
                value={props.title}
                className="form-control mb-4"
                placeholder="Title"
              />
              <input
                type="text"
                name="tag"
                onChange={props.tagOnChange}
                value={props.tag}
                placeholder="URL"
                className="form-control"
              />
              <input
                type="text"
                name="tag"
                onChange={props.tagOnChange}
                value={props.tag}
                placeholder="Tag"
                className="form-control"
              />
              <br />
              <button
                type="submit"
                className="btn btn-outline-dark waves-effect"
              >
                Add Link
              </button>
            </form>
          </div>
        </section>
      </div>
    </div>
  );
}

function SharelinkPage() {
  const [title, setTitle] = useState("");
  const [url, setUrl] = useState("");
  const [tag, setTag] = useState("");

  function titleOnChange(event) {
    setTitle(event.target.value);
  }
  function urlOnChange(event) {
    setUrl(event.target.value);
  }
  function tagOnChange(event) {
    setTag(event.target.value);
  }
  return (
    <div className="container">
      <h1>Sharelink Page</h1>

      <AddLink />
      <FilterInput />
      <LinkTable />
    </div>
  );
}

export default SharelinkPage;
